<?php/* sg_socialbar 131029 symstroem GbR-TN, Lizensiert unter GPL: http://www.gnu.org/licenses/gpl-3.0.html */    /*     * bereitstellung der daten für hauptsocialbar, und ggfs fuer sub_socials (artikel oder newsseite)     */    $datei = PFAD_ROOT . PFAD_EXPORT . 'main_socials.txt'; // besser waere es aus den $oplugin_einstellungsarray zu nehmen    if (isset($_GET['exclusive_content']) && $_GET['exclusive_content'] == '1')		$smarty->assign('exclusiveContent', 'true');        $jsonString = file_get_contents($datei);            $json = json_decode($jsonString, true);        $smarty->assign('sg_social_likes', $json['likeCount']);    $smarty->assign('sg_social_plusones', $json['plusOneCount']);    $smarty->assign('sg_social_follower', $json['followerCount']);              if (gibSeitenTyp() === 1 || gibSeitenTyp() === 7){ //1=artikel; 7=news                $smarty->assign('sg_shop_url',URL_SHOP);        $smarty->assign('sg_frontend_url', $oPlugin->cFrontendPfadURL);		        if (gibSeitenTyp() === 1){            $smarty->assign('sg_ist_artikel',true);        }        elseif (gibSeitenTyp() === 7){            $smarty->assign('sg_ist_news',true);        }		            }?>