<?php    /* sg_socialbar 131029 symstroem GbR-TN, Lizensiert unter GPL: http://www.gnu.org/licenses/gpl-3.0.html */    /*     * bereitstellung der daten für hauptsocialbar, und ggfs fuer sub_socials (artikel oder newsseite)     */    require_once(PFAD_ROOT . PFAD_CLASSES . "class.JTL-Shop.Jtllog.php");    if (isset($_GET['exclusive_content']) && $_GET['exclusive_content'] == '1')		$smarty->assign('exclusiveContent', 'true');            $smarty->assign('sg_frontend_url', $oPlugin->cFrontendPfadURL);    $smarty->assign('sg_shop_url',URL_SHOP);        if ($oPlugin->oPluginEinstellungAssoc_arr['screenName'] === 'XXX' || strlen($oPlugin->oPluginEinstellungAssoc_arr['screenName']) === 0){        Jtllog::writeLog('Socialbar-Plugin: Falscher Twitter ScreenName angegeben.', JTLLOG_LEVEL_ERROR);    }        $smarty->assign('sg_tw_screenName', $oPlugin->oPluginEinstellungAssoc_arr['screenName']);    if (gibSeitenTyp() === 1 || $smarty->_tpl_vars['AktuelleSeite'] === 'NEWSDETAIL'){ //1=artikel; 7=news        if (gibSeitenTyp() === 1){            $smarty->assign('sg_ist_artikel',true);        }        elseif ($smarty->_tpl_vars['AktuelleSeite'] === 'NEWSDETAIL'){            $smarty->assign('sg_ist_news',true);        }           }?>